import React from "react";
import "./App.css";
import * as data from "./data.json";
import * as dataWeek from "./data7d.json";
import { format, isSameDay, fromUnixTime, isWeekend } from "date-fns";

const advice = {
  Rain: "Better take an umbrella with you!",
  Drizzle: "Don't worry, it's just a few drops.",
  Clear: "Clear sky, clear mind.",
};

const parseCurrentWeather = (raw) => {
  return {
    date: raw.current.dt,
    temperature: raw.current.temp,
    description: raw.current.weather[0].description,
    iconURL: `http://openweathermap.org/img/wn/${raw.current.weather[0].icon}@4x.png`,
  };
};

const parseWeekendWeather = (raw) => {
  const weekend = raw.daily.filter((day) => isWeekend(fromUnixTime(day.dt)));
};

function WeatherDisplay({ date, description, temperature, iconURL }) {
  const day = isSameDay(new Date(), date) ? "Today" : format(date, "EEEE");
  return (
    <div>
      <div className="Date">
        <h2>{day}</h2>
        <p>{format(date, "PP")}</p>
      </div>
      <img src={iconURL} alt="icon" />
      <p>{description}</p>
      <p id="temperature">{temperature}</p>
      <p>{advice[description]}</p>
    </div>
  );
}

function App() {
  const [showWeekend, setShowWeekend] = React.useState(true);
  return (
    <div className="App">
      <header className="App-header">
        <div className="Card">
          <WeatherDisplay
            date={fromUnixTime(data.dt)}
            description={data.weather[0].main}
            temperature={data.main.temp}
          />
        </div>
      </header>
    </div>
  );
}

export default App;
