import React from "react";
import "./App.css";
import * as data from "./data.json";
import * as moment from "moment";

const advice = {
  Rain: "Better take an umbrella with you!",
  Drizzle: "It's just a few drops.",
  Clear: "Clear sky, clear mind.",
};

function WeatherDisplay({ date, description, temperature }) {
  const day = date == moment();
  return (
    <div>
      <p>Today</p>
      <p>{date.format("MMM Do YYYY")}</p>
      <p>{description}</p>
      <p>{temperature}</p>
      <p>{advice[description]}</p>
    </div>
  );
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <div className="Card">
          <WeatherDisplay
            date={moment()}
            description={data.weather[0].main}
            temperature={data.main.temp}
          />
        </div>
      </header>
    </div>
  );
}

export default App;
