import React from "react";
import "./App.css";
import * as data from "./data.json";
import * as dataWeek from "./data7d.json";
import { format, isSameDay, fromUnixTime, isWeekend } from "date-fns";

const advice = {
  "10d": "Better take an umbrella with you!",
  "01d": "Clear sky, clear mind.",
  "03d": "A gray day provides the best light.",
};

const parseDay = (day) => {
  return {
    date: fromUnixTime(day.dt),
    temperature: arrAvg(Object.values(day.temp)),
    description: day.weather[0].description,
    icon: day.weather[0].icon,
  };
};

const arrAvg = (arr) => arr.reduce((a, b) => a + b, 0) / arr.length;

const parseWeekendWeather = (raw) => {
  const parsed = raw.daily.map((day) => parseDay(day));
  return parsed.filter((day) => isWeekend(day.date));
};

function WeatherDisplay({ date, description, temperature, icon }) {
  const day = isSameDay(new Date(), date) ? "Today" : format(date, "EEEE");
  const iconURL = `http://openweathermap.org/img/wn/${icon}@4x.png`;
  return (
    <div>
      <div className="Date">
        <h3>{day}</h3>
        <p>{format(date, "PP")}</p>
      </div>
      <img src={iconURL} alt="icon" />
      <p>{description}</p>
      <p id="temperature">{temperature}</p>
      <p>{advice[icon]}</p>
    </div>
  );
}

function App() {
  const [showWeekend, setShowWeekend] = React.useState(true);
  const todayData = parseDay(dataWeek.default.current);
  const weekendData = parseWeekendWeather(dataWeek.default);
  const sat = weekendData[0];
  console.log(sat);
  console.log(todayData);
  return (
    <div className="App">
      <header className="App-header">
        <div className="Card">
          <WeatherDisplay {...sat} />
        </div>
      </header>
    </div>
  );
}

export default App;
